#!C:\Python34\pythonimport cgiimport sysimport pymysqlprint ("Content-Type:text/html \r\n\r\n")try:  con=pymysql.connect(user="root",password="",host="",database="buses_chavarria")  form = cgi.FieldStorage()  #ruta frecuencia    ini=form.getvalue('ini')  fin=form.getvalue('fin')  hora=form.getvalue('hora')    ru_fre=con.cursor()  ru_fre.execute("""select ru_fr.cod,of1.nombre,of2.nombre,fre.fecuencia FROM ruta_frecuencia ru_fr                   inner join ruta ru on ru_fr.cod_ruta=ru.cod                  inner join frecuencia fre on ru_fr.cod_frecuencia=fre.cod                  inner join oficina of1 on ru.oficina_origen=of1.cod                  inner join oficina of2 on ru.oficina_destino=of2.cod                  where of1.nombre='"""+ini+"""' and of2.nombre='"""+fin+"""' and fre.fecuencia='"""+hora+"""'                    """)  codrutafre=""  for art in ru_fre:    codrutafre=(str(art[0]))  #chofer  chofer=form.getvalue('chofer')  choferaux=form.getvalue('choferaux')  fecha=form.getvalue('fecha')  #bus  bu=con.cursor()  bu.execute("""select cod,placa FROM bus                   where placa='"""+str(form.getvalue('bus'))+"'")  bus=""  for art in bu:    bus=(str(art[0]))  #piso  piso=""  if str(form.getvalue('pas2')).upper()=='NONE':    piso=str(form.getvalue('pas1')).upper()  else:    piso=str(form.getvalue('pas1')).upper()+"-"+str(form.getvalue('pas2')).upper()  #usuario      usu=con.cursor()  p=usu.execute("select * from usuario WHERE pasword=MD5('"+str(form.getvalue('usu')).upper()+"')")  if p==1:     usuario=""      for art in usu:      usuario=str(art[0])    cad="'"+str(fecha).upper()+"','"+str(piso).upper()+"','"+str(codrutafre).upper()+"','"+str(bus).upper()+"','"+str(chofer).upper()+"','"+str(choferaux).upper()+"','"+str(usuario).upper()+"'"    cursor=con.cursor()    cursor.execute("INSERT INTO programar_venta(frecha_viaje,pasajes,cod_ruta_frecuencia,cod_bus,chofer,choferaux,cod_usuario) VALUES ("+cad+")")    con.commit()    con.close()    print("<script>location.href='../../administracion/programacion.py'</script>")  else:    print("<script>alert('Usuario Incorrecto')</script><script>location.href='../../administracion/programacion.py'</script>")except:  print("<script>location.href='../../error/error.py'</script>")