#!C:\Python34\pythonimport cgiimport sysimport pymysqlprint ("Content-Type:text/html \r\n\r\n")try:	form = cgi.FieldStorage()	nombre=form.getvalue('nombre')	direccion=form.getvalue('direccion')	telefono=form.getvalue('telefono')	celular=form.getvalue('celular')	horario=form.getvalue('hora_i')+" - "+form.getvalue('hora_f')	cad="'"+str(nombre).upper()+"','"+str(direccion).upper()+"','"+str(telefono).upper()+"','"+str(celular).upper()+"','"+str(horario).upper()+"'"	con=pymysql.connect(user="root",password="",host="",database="buses_chavarria")	cursor=con.cursor()	cursor.execute("INSERT INTO oficina(nombre,direccion,telefono,celular,horario_atencion) VALUES ("+cad+")")	con.commit()	con.close()	print("<script>location.href='../../administracion/oficinas.py'</script>")except:	print("<script>location.href='../../error/error.py'</script>")